import React, {useState} from 'react';
import './Cam.css';
import Webcam from "react-webcam";
import Tesseract from "tesseract.js";
import Container from 'react-bootstrap/Container';

const videoConstraints = {
    width: 440,
    height: 400,
    facingMode: "user"
};

export default function Cam() {
    const webcamRef = React.useRef(null);
    const [text, setText] = useState("");
    const [toggle, setToggle] = useState(true);

    const msg = new SpeechSynthesisUtterance();

    const handleClick = React.useCallback (() => {
            const imageSrc = webcamRef.current.getScreenshot();
            Tesseract.recognize(
                imageSrc, 'eng',
                {
                    logger: m => m
                }
            )
                .catch (x => {
                    console.error(x);
                })
                .then(result => {
                    let txt = "";
                    console.log(result.data.text + ": " + result.data.confidence);
                    if (result.data.confidence > 50) {
                        txt = result.data.text.replaceAll('[^\w\d\s.,!?]', '');
                        console.log(txt);
                    }

                    console.log('speech: ' + text);
                    if (txt.length > 0) {
                        setText(txt);
                        msg.text = txt;
                        if (toggle) window.speechSynthesis.speak(msg);
                    }
                })
            new Promise(resolve => setTimeout(resolve, 20)).then(r => {});
        }, [webcamRef.current, text, toggle]);

    // async function correct(txt) {
    //     const options = {
    //         method: 'POST',
    //         headers: {
    //             'content-type': 'application/json',
    //             'X-RapidAPI-Key': 'SIGN-UP-FOR-KEY',
    //             'X-RapidAPI-Host': 'jspell-checker.p.rapidapi.com'
    //         },
    //         body: '{"language":"enUS","fieldvalues":"thiss is intresting","config":{"forceUpperCase":false,"ignoreIrregularCaps":false,"ignoreFirstCaps":true,"ignoreNumbers":true,"ignoreUpper":false,"ignoreDouble":false,"ignoreWordsWithNumbers":true}}'
    //     };

    //     fetch('https://jspell-checker.p.rapidapi.com/check', options)
    //         .then(response => response.json())
    //         .then(response => console.log(response))
    //         .catch(err => console.error(err));
    // }

    return (
        <Container className={"my-[40px] justify-center items-center flex"}>
            <div className="App">
                <main className="App-main">
                    <h1 className={"font-bold font-[30px] mt-[15px]"}>Extracted Text</h1>
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        videoConstraints={videoConstraints} />
                    <p className="text-box extractedText flex justify-center"> {text} </p>
                    <button onClick={()=> {setToggle(!toggle); handleClick();}} className={"h-[50]"}>
                        {toggle?'Start Reading':'Stop Reading'}
                    </button>
                </main>
            </div>
        </Container>
    );
}
